const $=e=>document.querySelector(e);$("#form").addEventListener("submit",e=>{e.preventDefault();const t=parseInt($("#number").value);isValidate(t)?putInDOM(t):putInDOM("Invalid Input")});const isValidate=e=>e>=0&&e<=100,putInDOM=e=>{if("number"==typeof e){const t=getGrade(e);$(".result").innerHTML=`<h3>Your grade is ${t}</h3>`}else $(".result").innerHTML=`<h3>${e}. Provide a number between 0-100</h3>`},getGrade=e=>e>=80?"A+":e>=70?"A":e>=60?"A-":e>=50?"B":e>=40?"C":e>=33?"D":"F";